mapper.select:
SELECT 

FROM date_tree a

WHERE a.id = #{id}

SELECT
FROM date_tree a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM date_tree a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE date_tree SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
